<?php // Functions 
function calculateGrade($score) { if ($score >= 90) return "A"; elseif ($score >= 80) return "B"; elseif ($score >= 70) return "C"; elseif ($score >= 60) return "D"; else return "F"; } function getGradePoints($grade) { switch ($grade) { case "A": return 4.0; case "B": return 3.0; case "C": return 2.0; case "D": return 1.0; case "F": return 0.0; default: return 0.0; } } function calculateGPA($grades) { if (empty($grades)) return 0; $totalPoints = 0; $totalCredits = 0; foreach ($grades as $grade) { $totalPoints += getGradePoints($grade); $totalCredits++; } return $totalCredits > 0 ? round($totalPoints / $totalCredits, 2) : 0; } 
//Initialize variables 
$studentName = ""; $grades = []; $message = ""; 
// Process form submission 
if ($_SERVER["REQUEST_METHOD"] == "POST") { $studentName = trim($_POST["studentName"] ?? ""); if (!empty($studentName)) { // Get scores for each subject 
$subjects = ["Math", "Science", "English", "History"]; foreach ($subjects as $subject) { $score = $_POST[strtolower($subject)] ?? ""; if (is_numeric($score) && $score >= 0 && $score <= 100) { $grade = calculateGrade($score); $grades[$subject] = [ "score" => $score, "grade" => $grade ]; } } if (count($grades) == count($subjects)) { $gpa = calculateGPA(array_column($grades, "grade")); $message = "success"; } else { $message = "Please enter valid scores (0-100) for all subjects."; } } else { $message = "Please enter a student name."; } } ?> <!DOCTYPE html> <html> <head> <title>Student Grade Calculator</title> <style> body { font-family: Arial, sans-serif; margin: 20px; } .container { max-width: 600px; margin: 0 auto; } .form-group { margin-bottom: 15px; } label { display: block; margin-bottom: 5px; font-weight: bold; } input { padding: 8px; border: 1px solid #ddd; border-radius: 4px; } button { background-color: #28a745; color: white; padding: 10px 20px; border: none; border-radius: 4px; cursor: pointer; } .error { color: red; margin-bottom: 10px; } .success { color: green; margin-bottom: 10px; } .results { margin-top: 20px; padding: 15px; background-color: #f8f9fa; border-radius: 4px; } table { width: 100%; border-collapse: collapse; margin-top: 10px; } th, td { border: 1px solid #ddd; padding: 8px; text-align: left; } th { background-color: #007bff; color: white; } </style> </head> <body> <div class="container"> <h1>Student Grade Calculator</h1> <?php if ($message == "error"): ?> <div class="error"><?php echo htmlspecialchars($message); ?></div> <?php endif; ?> <form method="POST" action=""> <div class="form-group"> <label for="studentName">Student Name:</label> <input type="text" id="studentName" name="studentName" value="<?php echo htmlspecialchars($studentName); ?>" required> </div> <h3>Enter Scores (0-100):</h3> <div class="form-group"> <label for="math">Math:</label> <input type="number" id="math" name="math" min="0" max="100" value="<?php echo $grades["Math"]["score"] ?? ""; ?>"> </div> <div class="form-group"> <label for="science">Science:</label> <input type="number" id="science" name="science" min="0" max="100" value="<?php echo $grades["Science"]["score"] ?? ""; ?>"> </div> <div class="form-group"> <label for="english">English:</label> <input type="number" id="english" name="english" min="0" max="100" value="<?php echo $grades["English"]["score"] ?? ""; ?>"> </div> <div class="form-group"> <label for="history">History:</label> <input type="number" id="history" name="history" min="0" max="100" value="<?php echo $grades["History"]["score"] ?? ""; ?>"> </div> <button type="submit">Calculate Grades</button> </form> <?php if ($message == "success"): ?> <div class="results"> <h2>Grade Report for <?php echo htmlspecialchars($studentName); ?></h2> <table> <thead> <tr> <th>Subject</th> <th>Score</th> <th>Grade</th> </tr> </thead> <tbody> <?php foreach ($grades as $subject => $data): ?> <tr> <td><?php echo htmlspecialchars($subject); ?></td> <td><?php echo $data["score"]; ?></td> <td><strong><?php echo $data["grade"]; ?></strong></td> </tr> <?php endforeach; ?> </tbody> </table> <p><strong>GPA: <?php echo calculateGPA(array_column($grades, "grade")); ?></strong></p> <?php $gpa = calculateGPA(array_column($grades, "grade")); if ($gpa >= 3.5) { echo "<p style='color: green;'>üéâ Excellent performance!</p>"; } elseif ($gpa >= 3.0) { echo "<p style='color: blue;'>üëç Good work!</p>"; } elseif ($gpa >= 2.0) { echo "<p style='color: orange;'>‚ö†Ô∏è Room for improvement.</p>"; } else { echo "<p style='color: red;'>‚ùå Needs significant improvement.</p>"; } ?> </div> <?php endif; ?> </div> </body> </html>